import React, { Component } from 'react';
import styled from 'styled-components';

const Styles = styled.div`

`;

const SizeRange = (state, resetArray) => {
  console.log(state)
  return(
    <Styles>
      <input 
        type="range" 
        min={5} 
        max={500} 
        value={state.value} 
        className="slider" 
        onChange={(event) => {
          console.log(event.target.value);
          resetArray(state.size);
        }} />
    </Styles>
    )
}

export default SizeRange
