import React from 'react';
import SizeRange from '../SizeRange/SizeRange';
import Buttons from '../Buttons/Buttons';
import {getMergeSortAnimations} from '../MergeSortAnimation/MeargeSortAnimation'
import './SortingVisualizer.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class SortingVisualizer extends React.Component{
	constructor(props) {
		super(props);

		this.state = {
			array: [],
			size: 5,
			color: 'green',
		}
	}
	componentDidMount(){
		this.resetArray(this.state.size);
	}

	handleOnChange = (value) => {
		this.setState({size: value})
	}

	resetArray = (value) => {
		const array = [];
		const color = 'green';
		for (let i = 0; i< value; i++){
			array.push(randomIntFromInterval(5, 700));
		}
		this.setState({array});
		document.getElementsByClassName('array-bar').backgroundColor = 'green';
	}

	mergeSort = () => {
		const {array, size} = this.state;
		const animations = getMergeSortAnimations(array);
		console.log(animations);
		for (let i = 0; i < animations.length; i++) {
			const arrayBars = document.getElementsByClassName('array-bar');
			const isColorChange = i % 3 !== 2;
			if (isColorChange) {
				const [barOneIdx, barTwoIdx] = animations[i];
				const barOneStyle = arrayBars[barOneIdx].style;
				const barTwoStyle = arrayBars[barTwoIdx].style;
				const color = i % 3 === 0 ? 'red' : 'turquoise';
				setTimeout(() => {
					barOneStyle.backgroundColor = color;
					barTwoStyle.backgroundColor = color;
				}, (i * 500)/size);
			} else {
				setTimeout(() => {
					const [barOneIdx, newHeight] = animations[i];
					const barOneStyle = arrayBars[barOneIdx].style;
					barOneStyle.height = `${newHeight}px`;
				}, (i * 500)/size);
			// setTimeout(()=>{}, 1000);
			}
		}
	}


	render() {
		const {array} = this.state;
		let { size } = this.state;
		return (
			<div>
				<Buttons resetArray={this.resetArray} mergeSort = {this.mergeSort} volume={size}/>
				<h2>Size range</h2>
				<SizeRange state={this.state} resetArray={this.resetArray} />
				<div className="array-container">					
					{
						array.map((value, idx) => (

										<div  
											className="array-bar" 
											key={idx} 
											style={{height: `${value}px`, width: `${600/size}px`}}></div>
										)
						)
					}
				</div>
			</div>
			);
		}
	}


//https://stackoverflow.com/questions/1527803/generating-random-whole-numbers-in-javascript-in-a-specific-range
function randomIntFromInterval(min, max) {
	return Math.floor(Math.random() * (max-min + 1) + min);
}
export default SortingVisualizer;
