import React from 'react'
import './SortingVisualizer.css'

class SortingVisualizer extends React.Component{
	constructor(props) {
		super(props);

		this.state = {
			array: [],
		}
	}
	componentDidMount(){
		this.resetArray();
	}

	resetArray(){
		const array = [];
		for (let i = 0; i< 400; i++){
			array.push(randomIntFromInterval(5, 900));
		}
		this.setState({array})
	}


	render() {
		const {array} = this.state;
		return (
			<div className="array-container">
				<button onClick={() => this.resetArray()}>Generate New Array</button>
				<button onClick={() => this.resetArray()}>Marge Sort</button>
				<button onClick={() => this.resetArray()}>Quick Sort</button>
				<button onClick={() => this.resetArray()}>Heap Sort</button>
				<button onClick={() => this.resetArray()}>Bubble Sort</button>
				<br>saral</br>
				{
					array.map((value, idx) => (
									<div  
										className="array-bar" 
										key={idx} 
										style={{height: `${value}px`}}></div>
									)
					)
				}
			</div>
			);
		}
	}


//https://stackoverflow.com/questions/1527803/generating-random-whole-numbers-in-javascript-in-a-specific-range
function randomIntFromInterval(min, max) {
	return Math.floor(Math.random() * (max-min + 1) + min);
}
export default SortingVisualizer;
